fun screenShot() {

    var urls = content.split("\"")
        .filter { it.contains("https://ngabbs.com/read.php") }
        .toHashSet().toMutableList()

    urls.forEachIndexed { index, s ->
        if (Desktop.isDesktopSupported()) {
            try {
                var file = File("C:\\Users\\legion\\Pictures\\Screenshots")
                var mengan = File(file, "mengan")
                var aimFile = File(mengan, urls[index].filter { it in '0'..'9' } + ".png")
                if (!aimFile.exists()) {

                    urls[index] += "&authorid=234462"
                    startChrome(urls[index])

                    TimeUnit.MILLISECONDS.sleep(7000)

                    var robot = Robot()
                    robot.autoDelay = 100
                    robot.keyPress(KeyEvent.VK_WINDOWS)
                    robot.keyPress(KeyEvent.VK_PRINTSCREEN)
                    robot.keyRelease(KeyEvent.VK_WINDOWS)
                    robot.keyRelease(KeyEvent.VK_PRINTSCREEN)

                    TimeUnit.MILLISECONDS.sleep(300)

                    var ss = file.listFiles().filter { it.name.lowercase(Locale.getDefault()).endsWith("png") }
                    if (ss.size == 1) {
                        println(ss[0].absolutePath)
                        Files.move(
                            Paths.get(ss[0].absolutePath),
                            Paths.get(aimFile.absolutePath)
                        )
                    } else {
                        exitProcess(0)
                    }

                }

            } catch (e: Exception) {
                e.printStackTrace();
            }

            if (index % 40 == 39) {
                killChromeProcess()
            }
        }
    }
}


