fun reply(reply: String) {
    var al = ""
    var sign = false
    reply.forEach {
        if (it == '<' || it == '[') {
            sign = true;
        } else if (it == '>' || it == ']') {
            sign = false
            al += " "
        } else if (!sign) {
            al += it
        }
    }

    var results = ArrayList<String>()
    al.split("\n").forEach {
        var i = ""
        it.split(" ").filter { !it.isNullOrBlank() }.forEachIndexed { index, s ->
            if (index > 2 && s != "未登录") {
                i += s
            }
        }
        results.add(i)
    }


    var mds = ArrayList<String>()

    results.filter {
        !it.isBlank()
                && !it.startsWith("AFADA")
                && it != "锁定删除"
    }.forEach {
        if (it.startsWith("ReplyPostby")) {
            mds.add(it.substring(2 + it.indexOf("):")))
        } else if (it.startsWith("Reply")) {
            mds.add(it.substring(1 + it.indexOf(")")))
        } else if (it.startsWith("TopicPostby")) {
            mds.add(it.substring(2 + it.indexOf("):")))
        } else {
            mds.add(it)
        }
    }

    mds.forEach{
        println("```"+it+"``` \r\n")
    }

}

fun startChrome(url: String) {
    var uri = java.net.URI.create(url);
    var dp = Desktop.getDesktop();
    if (dp.isSupported(java.awt.Desktop.Action.BROWSE)) {
        dp.browse(uri);
    }
}

fun killChromeProcess() {
    var processBuilder = ProcessBuilder("tasklist")
    var process = processBuilder.start()
    var inputStream = process.getInputStream()
    var reader = BufferedReader(InputStreamReader(inputStream))
    var line: String? = reader.readLine()
    while (line != null) {
        if (line.contains("chrome")) {
            var split = line.split(" ").filter { it.isNotEmpty() }
            if (split.size >= 2) {
                var processBuilder = ProcessBuilder("taskkill", "/F", "/PID", split[1])
                var process = processBuilder.start()
                process.waitFor()
            }

        }
        line = reader.readLine()
    }
}

